import React, { useState } from 'react';
import axios from 'axios';
export default function App(){
  const [destination,setDestination] = useState('Bengaluru');
  const [budget,setBudget] = useState(300);
  const [userId,setUserId] = useState('ajay');
  const [plan,setPlan] = useState(null);
  const [loading,setLoading] = useState(false);
  const [message,setMessage] = useState('');
  const createPlan = async () => {
    setLoading(true);
    try{
      const resp = await axios.post('/api/plan', {user_id:userId, origin:'Salem', destination, start_date:'2025-12-10', end_date:'2025-12-12', budget});
      setPlan(resp.data);
    }catch(e){ alert('Plan failed: '+e.message); } finally { setLoading(false); }
  };
  return (<div style={{maxWidth:900,margin:'2rem auto'}}><h1>Travel Concierge (Gemini)</h1>
    <div style={{display:'flex',gap:8}}>
      <input value={userId} onChange={e=>setUserId(e.target.value)} />
      <input value={destination} onChange={e=>setDestination(e.target.value)} />
      <input type='number' value={budget} onChange={e=>setBudget(Number(e.target.value))} />
      <button onClick={createPlan} disabled={loading}>{loading?'...':'Plan Trip'}</button>
    </div>
    {plan && (<div style={{marginTop:20}}>
      <h3>Itinerary</h3><pre>{JSON.stringify(plan.itinerary,null,2)}</pre>
      <h3>Hotels</h3><ul>{plan.hotels.map((h,i)=>(<li key={i}>{h.name} - {h.price_per_night}</li>))}</ul>
      <h3>Total</h3><div>{plan.total_estimated_cost}</div>
    </div>)}
  </div>);
}
